import { useState } from "react";
import { useNavigate, useParams } from "react-router-dom"; // Assurez-vous d'importer useParams
import productService from "../../../services/productService";
import axios from 'axios';



async function AddProduct() {
  try {
    const response = await axios.post('http://localhost:8060/product/ad/img', data);
    // Traiter la réponse réussie ici
    console.log(response.data);
  } catch (error) {
    // Gérer l'erreur ici
    console.error('Erreur lors de la requête:', error.message);
    // Vous pouvez également afficher un message d'erreur à l'utilisateur
  }
  
  const [data, setData] = useState({});
  const [image, setImage] = useState({});
  const [categories, setCategories] = useState([]);
  const navigate = useNavigate();
  const { id } = useParams();

  const onChangeHandler = (e) => {
    setData({
      ...data,
      [e.target.name]: e.target.value,
    });
    console.log(data);
  };

  const onChangeImage = (e) => {
    e.preventDefault();
    setImage(e.target.files);
  };

  const onSubmitHandler = (e) => {
    const formdata = new FormData();
    e.preventDefault();
    formdata.append("ref", data.ref);
    formdata.append("id", data.id);
    formdata.append("name", data.name);
    formdata.append("price", data.price);
    formdata.append("description", data.description);
    formdata.append("quantité", data.quantité);
    formdata.append("Marque", data.Marque);
    formdata.append("category", data.category);
    formdata.append("pourcentage", data.pourcentage);
    formdata.append("createdAt", data.createdAt);
    formdata.append("dateDebutPromotion", data.dateDebutPromotion);
    formdata.append("dateFinPromotion", data.dateFinPromotion);
    formdata.append("instock", data.inStock);
    for (let i = 0; i < image?.length; i++) {
      formdata.append("photos", image[i]);
    }

    productService 
      .create(formdata)
      .then((res) => {
        console.log(res);
        navigate("/listProd")
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const getALL = () =>{
    categoryService.getAll().then((res)=>{
      console.log("res.data.datares.data.datares.data.datares.data.data",res.data.data)
      setcategories(res.data.data)
    })
    .catch((err)=>{
      console.log(err);
    })
  }
  useEffect(()=>{
    getALL()
  },[]);

    return(
        <>
<div className="col-12 grid-margin stretch-card">
  <div className="card"  style={{width: '86%',minWidth: '76%'}}>
    <div className="card-body">
      <h4 className="card-title">Add Product</h4>
      <p className="card-description">
        Add Product
      </p>
      <form className="forms-sample"  onSubmit={onSubmitHandler}>

 {/*ID*/}
 <div className="form-group">
                <label htmlFor="exampleInputName1">ID</label>
                <input type="Number" onChange={onChangeHandler} name="id" value={data.id} className="form-control" id="exampleInputName1" placeholder="id" />
              </div>
              {/*Ref*/}
 <div className="form-group">
                <label htmlFor="exampleInputName1">Ref</label>
                <input type="text" onChange={onChangeHandler} name="ref" value={data.ref} className="form-control" id="exampleInputName1" placeholder="REF" />
              </div>

              {/*Name*/}
              <div className="form-group">
                <label htmlFor="exampleInputName1">Name</label>
                <input type="text" name="name" className="form-control" value={data.name} id="exampleInputName1" onChange={onChangeHandler} placeholder="name" />
              </div>
                 {/*Price*/}
                 <div className="form-group">
                <label htmlFor="exampleInputName1">Price</label>
                <input type="Number" onChange={onChangeHandler} name="price" value={data.price} className="form-control" id="exampleInputName1" placeholder="price" />
              </div>
                 {/*description*/}
                 <div className="form-group">
                <label htmlFor="exampleInputName1">Description</label>
                <input type="text" onChange={onChangeHandler} name="description" value={data.description} className="form-control" id="exampleInputName1" placeholder="write here your description" />
              </div>
                {/*Quantité*/}
              <div className="form-group">
                <label htmlFor="exampleTextarea1">Quantité</label>
                <input type="Number" name="quantité" value={data.quantité} className="form-control" onChange={onChangeHandler} id="exampleInputName1" placeholder="Quantity" />
              </div>
                 {/*marque*/}
                 <div className="form-group">
                <label htmlFor="exampleInputName1">Marque</label>
                <input type="text" onChange={onChangeHandler} name="Marque" value={data.Marque} className="form-control" id="exampleInputName1" placeholder="Marque" />
              </div>

          
        
          
               {/*pourcentage*/}
               <div className="form-group">
                <label htmlFor="exampleInputName1">Pourcentage</label>
                <input type="text" onChange={onChangeHandler} name="pourcentage" value={data.pourcentage} className="form-control" id="exampleInputName1" placeholder="pourcentage" />
              </div>

               {/*createdAt*/}
               <div className="form-group">
                <label htmlFor="exampleInputName1">Created At</label>
                <input type="Date" name="createdAt" className="form-control" value={data.createdAt} id="exampleInputName1" onChange={onChangeHandler} placeholder="Date" />
              </div>
               {/*Date Debut de Promo*/}
               <div className="form-group">
              {/*Category*/}
         <div className="form-group">
         <label htmlFor="exampleInputName1">Category</label>
                 <select  name="category" onChange={onChangeHandler}  >
                 {categories?.map((i) => {
                 return (
                <option value={i._id} key={i._id}>
                  {i.name}
                    </option>
                       );
                      })}
                 </select>
              </div>   <label htmlFor="exampleInputName1">Date Debut de Promo</label>
                <input type="Date" name="dateDebutPromotion" className="form-control" value={data.dateDebutPromotion} id="exampleInputName1" onChange={onChangeHandler} placeholder="dateDebutPromotion" />
              </div>
               {/*Date Fin de Promo*/}
               <div className="form-group">
                <label htmlFor="exampleInputName1">Date Fin de Promo</label>
                <input type="Date" name="dateFinPromotion" className="form-control" value={data.dateFinPromotion} id="exampleInputName1" onChange={onChangeHandler} placeholder="dateFinPromotion" />
              </div>
              {/*in Stock*/}
              <div className="form-group">
                <label htmlFor="exampleInputName1">In Stock</label>
                <input type="boolean" name="inStock" className="form-control" value={data.inStock} id="exampleInputName1" onChange={onChangeHandler} placeholder="inStock" />
              </div>
           

                


              {/*Upload File*/}
              <div className="form-group">
                <label>File upload</label>
                <div className="input-group col-xs-12">
                 
                  <input type="file" name="gallery" className="form-control file-upload-info" onChange={onChangeImage}  placeholder="Upload Image" />
                  <span className="input-group-append">


                    
                  </span>

                </div>
              </div>

       

        
       


        <button type="submit" className="btn btn-primary mr-2">Submit</button>
        <button className="btn btn-light">Cancel</button>
      </form>
    </div>
  </div>
</div>
        </>
    )
}
export default AddProduct;
