import http from './axiosContext'

const create = (data) => {
    return http.post('order/create', data)
}

const getall = () => {
    return http.get('/Commande/getAll')
}

const update = (id , data) =>{
    return http.put(`/Commande/${id}` , data)
}

const deletecommande = (id) => {
    return http.delete(`/Commande/${id}`)
}

const getbyid = (id) => {
    return http.get(`/Commande/${id}`)
}

export default {create , update , getbyid , deletecommande, getall}
