import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

import Swal from 'sweetalert2';
import productService from "../../../services/productService";
import OrdersService from "../../../services/OrdersService";
import panierService from "../../../services/panierService";


function UpdateProduct() {
  const { id } = useParams();
  const [data, setData] = useState({})
  const navigate = useNavigate()

  const handleChanges = (id) => {
    setData({
      ...data,
      [id.target.name]: id.target.value
    })
  }


 
  const onSubmit = (e) =>{
    const formdata=new FormData()
    e.preventDefault()
    formdata.append("orderDate",data.orderDate)
    formdata.append("client",data.client)
    formdata.append("product",data.product)
    formdata.append("panier",data.panier)
    formdata.append("totalPrice",data.totalPrice)
    formdata.append("paymentMethod",data.paymentMethod)
    formdata.append("deliveryAddress",data.deliveryAddress)
    formdata.append("quantity",data.quantity)
     

    OrdersService.create(formdata)

    .then((res)=>{
      console.log(res)

    })
    .catch((err)=>{
      console.log(err)
    })
  };
 
  useEffect(() => {
    OrdersService.getbyid(id).then((res) => {
      console.log("OrdersService.getbyid", res)
      setData(res.data.data)
    })
  }, [])
  const [product, setproduct] = useState();

  const getAlll = () => {
    productService.getall().then((res) => {
      console.log(res.data.data)
      setproduct(res.data.data)
    })
      .catch((err) => {
        console.log(err)
      })
  }
  useEffect(() => {
    getAlll()
  }, []);
  const [panier, setpanier] = useState();

  const getAll = () => {
    panierService.getall().then((res) => {
      console.log(res.data.data)
      setpanier(res.data.data)
    })
      .catch((err) => {
        console.log(err)
      })
  }
  useEffect(() => {
    getAll()
  }, []);
  const fetchPanier = () => {
    axios.get(`http://localhost:8060/cart/${panierId}`)
      .then((response) => {
        setPanier(response.data);
      })
      .catch((error) => {
        console.error(error);
        setpanier(null);
      });
  };



  return (
    <>
      <div className="col-12 grid-margin stretch-card">
        <div className="card" style={{ width: '86%', minWidth: '76%' }}>
          <div className="card-body">
            <h4 className="card-title">Add Product</h4>
            <p className="card-description">
              Add Product
            </p>

            <form className="forms-sample" onSubmit={onSubmit}>
                   {/*orderDate*/}
        <div className="form-group">
          <label htmlFor="exampleInputName1">Order Date</label>
          <input type="Date" name="orderDate" onChange={onChangeHandler} className="form-control" id="exampleInputName1" placeholder="orderDate" />
        </div>
        
        {/*client*/}
        <div className="form-group">
          <label htmlFor="exampleInputName1">Client</label>
          <input type="id" name="client" className="form-control"  onChange={onChangeHandler} id="exampleInputName1" placeholder="client" />
        </div>

         {/*product*/}
         <div className="form-group">
          <label htmlFor="exampleInputName1">Product</label>
          <input type="id" name="product" onChange={onChangeHandler}  className="form-control" id="exampleInputName1" placeholder="product" />
        </div>
        
      <div className="form-group">
        <label htmlFor="exampleInputName1">Panier ID</label>
        <input type="text" name="panierId" onChange={onChangeHandler} className="form-control" id="exampleInputName1" placeholder="Panier ID" />
      </div>

      <button onClick={fetchPanier} className="btn btn-primary">Récupérer le panier</button>


         {/*totalPrice*/}
         <div className="form-group">
          <label htmlFor="exampleInputName1">total Price</label>
          <input type="Number" name="totalPrice" onChange={onChangeHandler} multiple className="form-control" id="exampleInputName1" placeholder="totalPrice" />
        </div>
        
        
        {/*paymentMethod*/}
        <div className="col-md-12">
          <div className="form-group row">
               <label className="col-sm-3 col-form-label">payment Method</label>
               <div className="col-sm-9">
               <input type="text" name="paymentMethod" className="form-control" onChange={onChangeHandler} id="exampleInputName1" placeholder="paymentMethod" /> 
                  
               </div>
           </div>
        </div>
        {/*deliveryAddress*/}
        <div className="form-group">
          <label htmlFor="exampleInputName1">delivery Address</label>
          <input type="text" name="tdeliveryAddress" onChange={onChangeHandler} multiple className="form-control" id="exampleInputName1" placeholder="deliveryAddress" />
        </div>
        {/*quantity*/}
        <div className="form-group">
          <label htmlFor="exampleInputName1">quantity</label>
          <input type="Number" name="quantity" onChange={onChangeHandler} multiple className="form-control" id="exampleInputName1" placeholder="tquantity" />
        </div>

      

              <button type="submit" className="btn btn-primary mr-2">Submit</button>
              <button className="btn btn-light">Cancel</button>
            </form> 

          </div>
        </div> 
      </div>
    </>
  )
}

export default UpdateProduct;
