import { useState } from "react";
import OrdersService from "../../../services/OrdersService";


function AddProduct() {
  const [data,setData] = useState();
  const [image,setImage] = useState();
  const onChangeHandler =  (e) => {
    setData({
      ...data, 
      [e.target.name] : e.target.value
    });
    console.log(data);
  };
  const onChangeImage=(e)=>{
    e.preventDefault()
    setImage(e.target.files)
  }

  const onSubmitHandler = (e) =>{
    const formdata=new FormData()
    e.preventDefault()
formdata.append("orderDate",data.orderDate)
formdata.append("client",data.client)
formdata.append("product",data.product)
formdata.append("panier",data.panier)
formdata.append("totalPrice",data.totalPrice)
formdata.append("paymentMethod",data.paymentMethod)
formdata.append(" deliveryAddress",data.deliveryAddress)
formdata.append("quantity",data.quantity)

    OrdersService.create(formdata)

    .then((res)=>{
      console.log(res)

    })
    .catch((err)=>{
      console.log(err)
    })
  };
 
    return(
        <>
<div className="col-12 grid-margin stretch-card">
  <div className="card"  style={{width: '86%',minWidth: '76%'}}>
    <div className="card-body">
      <h4 className="card-title">Add Orders</h4>
      <p className="card-description">
        Add Orders
      </p>
      <form className="forms-sample"  onSubmit={onSubmitHandler}>

        {/*orderDate*/}
        <div className="form-group">
          <label htmlFor="exampleInputName1">Order Date</label>
          <input type="Date" name="orderDate" onChange={onChangeHandler} className="form-control" id="exampleInputName1" placeholder="orderDate" />
        </div>
        
        {/*client*/}
        <div className="form-group">
          <label htmlFor="exampleInputName1">Client</label>
          <input type="id" name="client" className="form-control" onChange={onChangeHandler} id="exampleInputName1" placeholder="client" />
        </div>

         {/*product*/}
         <div className="form-group">
          <label htmlFor="exampleInputName1">Product</label>
          <input type="id" name="product" onChange={onChangeHandler} className="form-control" id="exampleInputName1" placeholder="product" />
        </div>
          {/*panier*/}
         <div className="form-group">
          <label htmlFor="exampleInputName1">Quantité</label>
          <input type="Number" name="qte" onChange={onChangeHandler} className="form-control" id="exampleInputName1" placeholder="quantité" />
        </div>

         {/*gallery*/}
         <div className="form-group">
          <label htmlFor="exampleInputName1">Photo</label>
          <input type="file" name="photos" onChange={onChangeImage} multiple className="form-control" id="exampleInputName1" placeholder="photo" />
        </div>
        
        
        {/*category*/}
        <div className="col-md-12">
          <div className="form-group row">
               <label className="col-sm-3 col-form-label">category</label>
               <div className="col-sm-9">
               <input type="text" name="category" className="form-control" onChange={onChangeHandler} id="exampleInputName1" placeholder="category" /> 
                  { <select className="form-control">
                     <option>Bague</option>
                     <option>Collier</option>
                     <option>Bracelet</option>
                     <option>Boucles</option>
                   </select> }
               </div>
           </div>
        </div>

      

       

        
       


        <button type="submit" className="btn btn-primary mr-2">Submit</button>
        <button className="btn btn-light">Cancel</button>
      </form>
    </div>
  </div>
</div>
        </>
    )
}
export default AddProduct;
