import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

import Swal from 'sweetalert2';
import productService from "../../../services/productService";
import OrdersService from "../../../services/OrdersService";


function UpdateProduct() {
  const { id } = useParams();
  const [data, setData] = useState({})
  const navigate = useNavigate()

  const handleChanges = (id) => {
    setData({
      ...data,
      [id.target.name]: id.target.value
    })
  }


 
  const onSubmit = (e) =>{
    const formdata=new FormData()
    e.preventDefault()
    formdata.append("orderDate",data.orderDate)
    formdata.append("client",data.client)
    formdata.append("product",data.product)
    formdata.append("panier",data.panier)
    formdata.append("totalPrice",data.totalPrice)
    formdata.append("paymentMethod",data.paymentMethod)
    formdata.append("deliveryAddress",data.deliveryAddress)
    formdata.append("quantity",data.quantity)
     

    OrdersService.create(formdata)

    .then((res)=>{
      console.log(res)

    })
    .catch((err)=>{
      console.log(err)
    })
  };
 
  useEffect(() => {
    OrdersService.getbyid(id).then((res) => {
      console.log("OrdersService.getbyid", res)
      setData(res.data.data)
    })
  }, [])
  const [product, setproduct] = useState();

  const getAlll = () => {
    productService.getall().then((res) => {
      console.log(res.data.data)
      setproduct(res.data.data)
    })
      .catch((err) => {
        console.log(err)
      })
  }
  useEffect(() => {
    getAlll()
  }, []);
  



  return (
    <>
      <div className="col-12 grid-margin stretch-card">
        <div className="card" style={{ width: '86%', minWidth: '76%' }}>
          <div className="card-body">
            <h4 className="card-title">Add Product</h4>
            <p className="card-description">
              Add Product
            </p>

            <form className="forms-sample" onSubmit={onSubmit}>
              {/*ID*/}
              <div className="form-group">
                <label htmlFor="exampleInputName1">ID</label>
                <input type="Number" onChange={handleChanges} name="id" value={data.id} className="form-control" id="exampleInputName1" placeholder="id" />
              </div>

              {/*Name*/}
              <div className="form-group">
                <label htmlFor="exampleInputName1">Name</label>
                <input type="text" name="name" className="form-control" value={data.name} id="exampleInputName1" onChange={handleChanges} placeholder="name" />
              </div>
                 {/*Price*/}
                 <div className="form-group">
                <label htmlFor="exampleInputName1">Price</label>
                <input type="Number" onChange={handleChanges} name="price" value={data.price} className="form-control" id="exampleInputName1" placeholder="price" />
              </div>
                {/*Quantité*/}
              <div className="form-group">
                <label htmlFor="exampleTextarea1">Quantité</label>
                <input type="Number" name="quantité" value={data.quantité} className="form-control" onChange={handleChanges} id="exampleInputName1" placeholder="Quantity" />
              </div>

              {/*category*/}
              <div className="form-group row">
                <label className="col-sm-3 col-form-label">category</label>

                <select className="form-control" onChange={handleChanges} name="category" >
                  {categories?.map((x) => {
                    return (
                      <option name="category" onChange={handleChanges} value={x._id}>
                        {x.name}
                      </option>
                    )
                  })}

                  
                </select>
              </div>
               {/*createdAt*/}
               <div className="form-group">
                <label htmlFor="exampleInputName1">Created At</label>
                <input type="Date" name="createdAt" className="form-control" value={data.createdAt} id="exampleInputName1" onChange={handleChanges} placeholder="Date" />
              </div>
              {/*in Stock*/}
              <div className="form-group">
                <label htmlFor="exampleInputName1">In Stock</label>
                <input type="boolean" name="inStock" className="form-control" value={data.inStock} id="exampleInputName1" onChange={handleChanges} placeholder="inStock" />
              </div>
             


              {/*Upload File*/}
              <div className="form-group">
                <label>File upload</label>
                <div className="input-group col-xs-12">
                 
                  <input type="file" name="gallery" className="form-control file-upload-info" onChange={handleChanges}  placeholder="Upload Image" />
                  <span className="input-group-append">


                    
                  </span>

                </div>
              </div>


              <button type="submit" className="btn btn-primary mr-2">Submit</button>
              <button className="btn btn-light">Cancel</button>
            </form> 

          </div>
        </div> 
      </div>
    </>
  )
}

export default UpdateProduct;
